class AllureConfig:    @staticmethod    def set_allure_environment(platformName, environment, projectName, endUrl = None, apiUrl = None, **kwargs):        env = {            'Operating.System': platformName,            'Environment': environment,            'Application': projectName,            'App.Path': endUrl,            'API.URL': apiUrl        }        env.update(kwargs)        return env    @staticmethod    def set_allure_executor(name, endUrl = None, buildName = None, reportName = None, **kwargs):        executor = {            "name": name,            'buildUrl': endUrl,            'buildName': buildName,            "reportName": reportName        }        executor.update(kwargs)        return executor